          if (newNode.type.name == 'notation_block') {
              cursorOffset += 1;
          }

          const cursorPos = nodePos + cursorOffset;
          const curSelection = TextSelection.create(tr.doc, cursorPos);
          tr.setSelection(curSelection);

          dispatch(tr);
          return true;
